import React, { Fragment } from 'react';
import { ListGroup, ListGroupItem, Card, Button } from 'react-bootstrap';
import { Activity } from '../../models/Activity';

interface Props {
	activities: Activity[];
}

function ActivityDashboard({activities}: Props) {
	return (
		<Fragment>
			<ListGroup>
				<ListGroupItem>
					{activities.map((activity: Activity) => (
						<Card key={activity.id} style={{ width: '60%' }}>
							<Card.Body>
								<Card.Header>{activity.category}</Card.Header>
								<Card.Title>{activity.title}</Card.Title>
								<Card.Img variant="top" src={`/assets/images/${activity.category}.jpg`} />
								<Card.Text>{activity.venue}</Card.Text>
								<Card.Footer>{activity.date} </Card.Footer>
								<Button variant="secondary" size="lg">
									View
								</Button>
							</Card.Body>
						</Card>
					))}
				</ListGroupItem>
			</ListGroup>
		</Fragment>
	);
}

export default ActivityDashboard;
