import React, { Fragment } from 'react';
import { ListGroup, ListGroupItem, Card, Button, Col, Row } from 'react-bootstrap';
import { Activity } from '../../../app/models/Activity';

interface Props {
	activities: Activity[];
}

function ActivityList({ activities }: Props) {
	return (
		<Fragment>
			<ListGroup>
				<ListGroupItem>
					{activities.map((activity: Activity) => (
						<Card key={activity.id} style={{ width: '60%' }}>
							<Card.Body>
								<Card.Img variant="top" src={`/assets/images/${activity.category}.jpg`} />
								<Card.Title>{activity.category}</Card.Title>
								<Card.Header>{activity.title}</Card.Header>

								<Card.Text>
									{activity.city}, {activity.venue}
								</Card.Text>

								<Card.Footer>
									<Row>
										<Col xs>{activity.date}</Col>
										<Col md>
											<Button variant="secondary" size="lg">
												View
											</Button>
										</Col>
									</Row>
								</Card.Footer>
							</Card.Body>
						</Card>
					))}
				</ListGroupItem>
			</ListGroup>
		</Fragment>
	);
}

export default ActivityList;
