import React, { Fragment, useEffect, useState } from 'react';
import './App.css';
import { Activity } from '../models/Activity';
import NavBar from './navbar/NavigationBar';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Container } from 'react-bootstrap';
import ActivityDashboard from '../../features/activities/activityDashboard/ActivityDashboard';
import agent from '../api/agent';
import Loader from './Loader';
import { useStore } from '../stores/store';
import { observer } from 'mobx-react-lite';
import HomePage from '../../features/home/HomePage';
import { Route } from 'react-router-dom';
import ActivityForm from '../../features/activities/activityForm/ActivityForm';

function App() {
	const { activityStore } = useStore();
	const [ activities, setActivities ] = useState<Activity[]>([]);
	const [ submitting, setSubmitting ] = useState(false);

	useEffect(
		() => {
			activityStore.loadActivities();
		},
		[ activityStore ]
	);

	function handleDeleteActivity(id: string) {
		setSubmitting(true);
		agent.Activities.delete(id).then(() => {
			setActivities([ ...activities.filter((x) => x.id !== id) ]);
			setSubmitting(false);
		});
	}

	if (activityStore.loadingInitial) {
		return <Loader />;
	}

	return (
		<Fragment>
			<NavBar />
			<Container fluid className="mainAppContainer">
				<ActivityDashboard activities={activityStore.activities} deleteActivity={handleDeleteActivity} />
			</Container>
		</Fragment>
	);
}

export default observer(App);
