import React from 'react';
import { Spinner, Modal, Container,Image } from 'react-bootstrap';

interface Props {
	inverted: boolean;
	content: string;
}

function Loader({ inverted = true, content = 'Loading...' }) {
	return (
		<Container fluid className="d-flex flex-column align-items-center loader-container">
            <Image src='/assets/images/'></Image>
			<Spinner animation="border" variant="primary" />
		</Container>
	);
}

export default Loader;
