import { action, makeObservable, observable } from 'mobx';

export default class ActivityStore {
	title = 'Hello from MobX Activity store';

	constructor() {
		makeObservable(this, {
			title: observable,
			setTitle: action.bound
		});
	}

	setTitle() {
		this.title = this.title + '!';
	}
}
