import React, { useState } from 'react';

import { Form, FormControl, FormGroup, FormLabel, Container, Button, Row } from 'react-bootstrap';
import { Activity } from '../../../app/models/Activity';

interface Props {
	activity: Activity | undefined;
	closeForm: () => void;
}

function ActivityForm({ activity: selectedActivity, closeForm }: Props) {

	const initialState = selectedActivity ?? {
		id: '',
		title: '',
		category: '',
		description: '',
		date: '',
		city: '',
		venue: ''
	}

	const [activity, setActivity] = useState(initialState);

	function handleSubmit(){
		console.log(activity);
	}

	function handleInputChange(){

	}

	return (
		<Container className="mb-3">
			<Form onSubmit={handleSubmit} autoComplete='off'>
				<FormGroup className="mb-2">
					<FormControl type="text" placeholder="Title" value={activity.title} name='title' onch/>
				</FormGroup>
				<FormGroup className="mb-2">
					<Form.Text placeholder="Descripcion" />
				</FormGroup>
				<FormGroup className="mb-2">
					<FormControl type="text" placeholder="Category" />
				</FormGroup>
				<FormGroup className="mb-2">
					<FormControl type="text" placeholder="Date" />
				</FormGroup>
				<FormGroup className="mb-2">
					<FormControl type="text" placeholder="City" />
				</FormGroup>
				<FormGroup className="mb-2">
					<FormControl type="text" placeholder="Venue" />
				</FormGroup>
				<FormGroup className="mb-2">
					<Row>
						<Button type="submit">Change</Button>
						<Button onClick={closeForm}>Cancel</Button>
					</Row>
				</FormGroup>
			</Form>
		</Container>
	);
}
export default ActivityForm;
