import React from 'react';
import { Badge, Card, CardGroup, CardImg, Container, Row, Button, Col } from 'react-bootstrap';
import { Activity } from '../../../app/models/Activity';
import ActivityForm from '../activityForm/ActivityForm';

interface Props {
	activity: Activity;

	cancelSelectActivity: () => void;
	openForm: (id: string) => void;
	closeForm: (id: string) => void;
	editMode: boolean;
}

function ActivityDetails({ activity, cancelSelectActivity, openForm, closeForm, editMode }: Props) {
	return (
		<Container className="mb-3">
			<Card style={{ width: '100%' }}>
				<Card.Body>
					{activity && <Card.Title>{activity.title}</Card.Title>}

					<CardImg src={`/assets/images/drinks.jpg`} />
					<Row>
						<Card.Text>{activity.description}</Card.Text>
					</Row>
					<Row>
						<Badge bg="secondary">{activity.category}</Badge>
					</Row>
					<Row>
						<Button size="sm" onClick={() => openForm(activity.id)}>
							Edit
						</Button>

						<Button size="sm" variant="outline-secondary" onClick={cancelSelectActivity}>
							Close View
						</Button>
					</Row>
				</Card.Body>
			</Card>
		</Container>
	);
}
export default ActivityDetails;
