import { makeAutoObservable } from 'mobx';
import { Activity } from '../models/Activity';

export default class ActivityStore {
	activities: Activity[] = [];
	selectedActivity: Activity | null = null;
	editMode = false;

	constructor() {
		makeAutoObservable(this);
	}
}
