import React, { Fragment, useEffect, useState } from 'react';
import './App.css';
import { Activity } from '../models/Activity';
import NavBar from './navbar/NavigationBar';
import axios from 'axios';

import 'bootstrap/dist/css/bootstrap.min.css';
import { Button, Card, CardGroup, Container, Image, ListGroup, ListGroupItem, Spinner, Stack } from 'react-bootstrap';

function App() {
	const [ selectedActivity, setSelectedActivity ] = useState<Activity>();
	const [ activities, setActivities ] = useState([]);

	useEffect(() => {
		axios.get('http://localhost:5000/api/activities').then((response) => {
			console.log(response);
			setActivities(response.data);
		});
	}, []);

	return (
		<Fragment>
			<NavBar />
			<Container fluid="sm">
				<CardGroup>
					{activities.map((activity: Activity) => (
						<Card key={activity.id} style={{ width: '1rem' }}>
							<Card.Body>
								<Card.Header>{activity.category}</Card.Header>
								<Card.Title>{activity.title}</Card.Title>
								<Card.Img variant="top" src={`/assets/images/${activity.category}.jpg`} />
								<Card.Text>{activity.venue}</Card.Text>
								<Card.Footer>{activity.date} </Card.Footer>
								<Button variant=''>View</Button>
							</Card.Body>
						</Card>
					))}
				</CardGroup>
			</Container>
		</Fragment>
	);
}

export default App;
