import React, { Fragment, useEffect, useState } from 'react';
import './App.css';
import { Activity } from '../models/Activity';
import NavBar from './navbar/NavigationBar';
import axios from 'axios';

import 'bootstrap/dist/css/bootstrap.min.css';
import {
	Button,
	Card,
	CardGroup,
	Col,
	Container,
	Image,
	ListGroup,
	ListGroupItem,
	Row,
	Spinner,
	Stack
} from 'react-bootstrap';
import ActivityDashboard from '../../features/activities/activityDashboard/ActivityDashboard';
import ActivityDetails from '../../features/activities/activityDetails/ActivityDetails';

function App() {
	const [ selectedActivity, setSelectedActivity ] = useState<Activity | undefined>(undefined);
	const [ activities, setActivities ] = useState<Activity[]>([]);
	const [ editMode, setEditMode ] = useState(false);

	useEffect(() => {
		axios.get<Activity[]>('http://localhost:5000/api/activities').then((response) => {
			console.log(response);
			setActivities(response.data);
		});
	}, []);

	function handleSelectActivity(id: string) {
		setSelectedActivity(activities.find((x) => x.id));
	}

	function handleCancelSelectActivity() {
		setSelectedActivity(undefined);
	}

function handleFormOpen(id: string){
	id ? handleSelectActivity(id) : h
}

	return (
		<Container fluid="md">
			<NavBar />
			<Row>
				<Col md="8">
					<ActivityDashboard
						activities={activities}
						selectedActivity={selectedActivity}
						selectActivity={handleSelectActivity}
						cancelSelectActivity={handleCancelSelectActivity}
					/>
				</Col>
				<Col md="4" id="colDetails" />
			</Row>
		</Container>
	);
}

export default App;
