import React from 'react';
import { Badge, Card, CardGroup, CardImg, Container, Row, Button, Col } from 'react-bootstrap';
import { Activity } from '../../../app/models/Activity';
import ActivityForm from '../activityForm/ActivityForm';

interface Props {
	activity: Activity;
	selectedActivity: (id: string) => void;
}

function ActivityDetails({ activity }: Props) {
	return (
		<Container className="mb-3">
			<Card style={{ width: '100%' }}>
				<Card.Body>
					{activity && <Card.Title>{activity.title}</Card.Title>}

					<CardImg src={`/assets/images/drinks.jpg`} />
					<Row>
						<Card.Text>Descripcion</Card.Text>
					</Row>
					<Row>
						<Badge bg="secondary">Categoria</Badge>
					</Row>
					<Row>
						<Button size="sm">Edit</Button>

						<Button size="sm" variant="outline-secondary">
							Cancel
						</Button>
					</Row>
				</Card.Body>
			</Card>

			<ActivityForm />
		</Container>
	);
}
export default ActivityDetails;
